export class AddHeaderInterceptor implements HttpInterceptor { intercept(req: HttpRequest<any>, next: HttpHandler): Observable<HttpEvent<any>> { const clonedRequest = req.clone({ headers: req.headers.set('miao-token', YOUR_GLOBAL_TOKEN) }); return next.handle(clonedRequest); }
} In the end, here is the resuable process flow. RESTful Request |
HttpSessionIdResolver |
(has token id)? | |-------(yes): get seesion by id from redis and reuse. ---------(no): jump to login page and create id by UUID

